// Toggle information box
var infoicon = document.getElementById("infoIconPresence");
infoicon.onclick = function() {
  var element = document.getElementById("informationBoxPresence");
  var other_element = document.getElementById("informationBoxAdjustedPresence");

  if (element === null && other_element === null) {
    ReactDOM.render(
        <div id="informationBoxPresence">
          <p> This control feature helps to automatically switch ON your devices when you arrive at your desk and switch them OFF after you have left your desk after some time.</p>
          <p> Click on icon to switch presence-based control ON / OFF. </p>
        </div>, document.getElementById('infoBoxPresence')
    )
  } else {
    ReactDOM.unmountComponentAtNode(document.getElementById('infoBoxPresence'));
  }
}

// Turning presence control off or on for Desktop
var desktop = document.getElementById("Desktop");
var desktopDropdown = document.getElementById("Desktopdropdown");
desktop.onclick = function() {
  if (desktop.className === "greenPresence") {
    if (window.confirm("You are switching off presence-based control for Desktop.")) {
      desktop.className = "redPresence";
      desktopDropdown.childNodes[0].value = "1 minute";
      desktopDropdown.childNodes[0].disabled = true;
      desktop_popup.classList.remove("visiblePresence");
      desktop_popup.classList.add("invisiblePresence")
    }
  } else {
    desktop.className = "greenPresence";
    desktopDropdown.childNodes[0].disabled = !desktopDropdown.childNodes[0].disabled;
  }
}


