class Basic extends Component {
  constructor(props) {
    super(props);
    let schedulerData = new SchedulerData(
      "2017-12-18",
      ViewTypes.Day,
      false,
      false,
      {
        checkConflict: true,
        recurringEventsEnabled: true,
        scrollToSpecialMomentEnabled: true, // Allow automatic scroll
        crossResourceMove: false // Cannot drag events between plug loads
      }, {
        getScrollSpecialMomentFunc: this.getScrollSpecialMoment, // Allow automatic scroll
        isNonWorkingTimeFunc: this.isNonWorkingTime // Set whole day to "NonWorkingTime", so that grey colour is consistent
      }
    );
    schedulerData.setResources(DemoData.resources); // Set the calendar plug loads to be the plug loads from DemoData
    schedulerData.setEvents(DemoData.events); // Set the calendar events to be the events from DemoData
    this.state = {
      viewModel: schedulerData
    };
    window.mondayCalendar = this
  }

  render() {
    const { viewModel } = this.state;
    return (
      <div>
        <div>
          <Scheduler
            schedulerData={viewModel}
            eventItemClick={this.eventClicked}
            updateEventStart={this.updateEventStart}
            updateEventEnd={this.updateEventEnd}
            moveEvent={this.moveEvent}
            newEvent={this.newEvent}
            conflictOccurred={this.conflictOccurred}
          />
        </div>
      </div>
    );
  }

